import React, { useState, useEffect } from 'react'
import { Col, Container, Row } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
import {
  getAllCategory,
  addCategory,
  updateCategories,
  deleteCategories as deleteCategoriesAction,
} from '../../../actions/actions'
import Input from '../../UI/input/Input.ui'
import NewModal from '../../UI/modal/modal'
import Layout from '../../layouts/header.layout'
import CheckboxTree from 'react-checkbox-tree'
import 'react-checkbox-tree/lib/react-checkbox-tree.css'
import { IoCheckbox, IoArrowDown, IoArrowForward } from 'react-icons/io5'

import { IoIosCheckboxOutline } from 'react-icons/io'

const Category = (props) => {
  const [categoryName, setCategoryName] = useState('')
  const [parentCategoryId, setParentCategoryId] = useState('')
  const [categoryImage, setCategoryImage] = useState('')
  const [updateCategoryModal, setUpdateCategoryModal] = useState(false)
  const [show, setShow] = useState(false)

  const [checked, setChecked] = useState([])
  const [expanded, setExpanded] = useState([])

  const [checkedArray, setCheckedArray] = useState([])
  const [expandedArray, setExpandedArray] = useState([])

  const [deleteCategoryModal, setDeleteCategoryModal] = useState(false)

  const category = useSelector((state) => state.category)
  const dispatch = useDispatch()

  const handleClose = () => {
    const form = new FormData()

    form.append('name', categoryName)
    form.append('parentId', parentCategoryId)
    form.append('categoryPicture', categoryImage)
    dispatch(addCategory(form))
    // window.location.reload(false)
    setCategoryName('')
    setParentCategoryId('')
    // const cat = {
    //   categoryName,
    //   parentCategoryId,
    //   categoryImage,
    // }

    // console.log(cat)

    setShow(false)
  }
  const handleShow = () => setShow(true)

  const renderCategories = (categories) => {
    let myCategories = []
    for (let category of categories) {
      myCategories.push({
        label: category.name,
        value: category._id,
        children:
          category.children.length > 0 && renderCategories(category.children),
      })
    }
    return myCategories
  }

  const createCategoryList = (categories, options = []) => {
    for (let category of categories) {
      options.push({
        value: category._id,
        name: category.name,
        parentId: category.parentId,
        type: category.type,
      })
      if (category.children.length > 0) {
        createCategoryList(category.children, options)
      }
    }

    return options
  }

  const handleCategoryImage = (e) => {
    setCategoryImage(e.target.files[0])
  }

  const updateCategory = () => {
    updateCheckedExpandedCategories()
    setUpdateCategoryModal(true)
  }

  const updateCheckedExpandedCategories = () => {
    const categories = createCategoryList(category.categories)
    const checkedArray = []
    const expandedArray = []
    checked.length > 0 &&
      checked.forEach((categoryId, index) => {
        const category = categories.find(
          (category, _index) => categoryId === category.value
        )

        //there is maybe a change a catgeory won't match, so just for safety we will have to do this
        category && checkedArray.push(category)
      })

    expanded.length > 0 &&
      expanded.forEach((categoryId, index) => {
        const category = categories.find(
          (category, _index) => categoryId === category.value
        )

        //there is maybe a change a catgeory won't match, so just for safety we will have to do this
        category && expandedArray.push(category)
      })

    //these two will help to modify the inputs in edit category
    setCheckedArray(checkedArray)
    setExpandedArray(expandedArray)
  }

  const handleCategoryInput = (key, value, index, type) => {
    if (type == 'checked') {
      const updatedCheckedArray = checkedArray.map((item, _index) =>
        index == _index ? { ...item, [key]: value } : item
      )
      setCheckedArray(updatedCheckedArray)
    } else if (type == 'expanded') {
      const updatedExpandedArray = expandedArray.map((item, _index) =>
        index == _index ? { ...item, [key]: value } : item
      )
      setExpandedArray(updatedExpandedArray)
    }
  }
  {
    /* Edit Categories */
  }

  const updateCategoriesForm = () => {
    const form = new FormData()
    expandedArray.forEach((item, index) => {
      form.append('_id', item.value)
      form.append('name', item.name)
      form.append('parentId', item.parentId ? item.parentId : '')
      form.append('type', item.type)
    })

    checkedArray.forEach((item, index) => {
      form.append('_id', item.value)
      form.append('name', item.name)
      form.append('parentId', item.parentId ? item.parentId : '')
      form.append('type', item.type)
    })

    dispatch(updateCategories(form)).then((result) => {
      if (result) {
        dispatch(getAllCategory())
      }
    })

    setUpdateCategoryModal(false)
  }

  const renderUpdateCategoriesModal = () => {
    return (
      <NewModal
        show={updateCategoryModal}
        handleClose={updateCategoriesForm}
        modalTitle={'Update Categories'}
        size="lg"
      >
        <Row>
          <Col>
            <h6>Expanded</h6>
          </Col>
        </Row>
        {expandedArray.length > 0 &&
          expandedArray.map((item, index) => (
            <Row key={index}>
              <Col>
                <Input
                  value={item.name}
                  placeholder={'Category Name'}
                  onChange={(e) =>
                    handleCategoryInput(
                      'name',
                      e.target.value,
                      index,
                      'expanded'
                    )
                  }
                />
              </Col>

              <Col>
                <select
                  className="form-control"
                  value={item.parentId}
                  onChange={(e) =>
                    handleCategoryInput(
                      'name',
                      e.target.value,
                      index,
                      'expanded'
                    )
                  }
                >
                  <option>select category</option>
                  {createCategoryList(category.categories).map((option) => (
                    <option key={option.value} value={option.value}>
                      {option.name}
                    </option>
                  ))}
                </select>
              </Col>
              <Col>
                <select className="form-control">
                  <option value="">Select type</option>
                  <option value="store">Store</option>
                  <option value="product">Product</option>
                  <option value="page">Page</option>
                </select>
              </Col>
            </Row>
          ))}

        <Row>
          <Col>
            <h6>Checked</h6>
          </Col>
        </Row>
        {checkedArray.length > 0 &&
          checkedArray.map((item, index) => (
            <Row key={index}>
              <Col>
                <Input
                  value={item.name}
                  placeholder={'Category Name'}
                  onChange={(e) =>
                    handleCategoryInput(
                      'name',
                      e.target.value,
                      index,
                      'checked'
                    )
                  }
                />
              </Col>

              <Col>
                <select
                  className="form-control"
                  value={item.parentId}
                  onChange={(e) =>
                    handleCategoryInput(
                      'name',
                      e.target.value,
                      index,
                      'checked'
                    )
                  }
                >
                  <option>select category</option>
                  {createCategoryList(category.categories).map((option) => (
                    <option key={option.value} value={option.value}>
                      {option.name}
                    </option>
                  ))}
                </select>
              </Col>
              <Col>
                <select className="form-control">
                  <option value="">Select type</option>
                  <option value="store">Store</option>
                  <option value="product">Product</option>
                  <option value="page">Page</option>
                </select>
              </Col>
            </Row>
          ))}

        {/* <input
            type="file"
            name="categoryImage"
            onChange={handleCategoryImage}
          ></input> */}
      </NewModal>
    )
  }

  const deleteCategory = () => {
    updateCheckedExpandedCategories()
    setDeleteCategoryModal(true)
  }

  const deleteCategories = () => {
    //get expandedCategory id
    const expandedByIdArray = expandedArray.map((item, index) => ({
      _id: item.value,
    }))

    //get checkedCategory id
    const checkedByIdArray = checkedArray.map((item, index) => ({
      _id: item.value,
    }))

    //merge the checked and expanded arrays by id
    const arraysByIds = expandedByIdArray.concat(checkedByIdArray)
    dispatch(deleteCategoriesAction(arraysByIds))
  }

  const renderDeleteCategoryModal = () => {
    // console.log('delete', checkedArray)

    return (
      <NewModal
        modalTitle="Confirm"
        show={deleteCategoryModal}
        handleClose={() => setDeleteCategoryModal(false)}
        buttons={[
          {
            label: 'No',
            color: 'primary',
            onClick: () => {
              alert('no')
            },
          },
          {
            label: 'Yes',
            color: 'danger',
            onClick: deleteCategories,
          },
        ]}
      >
        <h5>Expanded</h5>
        {expandedArray.map((item, index) => (
          <span key={index}>{item.name}</span>
        ))}
        <h5>Checked</h5>
        {checkedArray.map((item, index) => (
          <span key={index}>{item.name}</span>
        ))}
      </NewModal>
    )
  }

  const renderAddCategoryModal = () => {
    return (
      <NewModal
        show={show}
        handleClose={handleClose}
        modalTitle={'Add New Category'}
      >
        <Input
          value={categoryName}
          placeholder={'Category Name'}
          onChange={(e) => setCategoryName(e.target.value)}
        />
        <select
          className="form-control"
          value={parentCategoryId}
          onChange={(e) => setParentCategoryId(e.target.value)}
        >
          <option>select category</option>
          {createCategoryList(category.categories).map((option) => (
            <option key={option.value} value={option.value}>
              {option.name}
            </option>
          ))}
        </select>
        <input
          type="file"
          name="categoryImage"
          onChange={handleCategoryImage}
        ></input>
      </NewModal>
    )
  }

  return (
    <div>
      <Layout sidebar>
        <Container>
          <Row>
            <Col md={12}>
              <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                <h3>Category</h3>
                <button onClick={handleShow}>Add</button>
              </div>
            </Col>
          </Row>
          <Row>
            <Col md={12}>
              {/* <ul>{renderCategories(category.categories)}</ul>
               */}

              <CheckboxTree
                nodes={renderCategories(category.categories)}
                checked={checked}
                expanded={expanded}
                onCheck={(checked) => setChecked(checked)}
                onExpand={(expanded) => setExpanded(expanded)}
                icons={{
                  check: <IoCheckbox />,
                  uncheck: <IoIosCheckboxOutline />,
                  halfCheck: <IoIosCheckboxOutline />,
                  expandClose: <IoArrowForward />,
                  expandOpen: <IoArrowDown />,
                }}
              />
            </Col>
          </Row>
          <Row>
            <Col>
              <button onClick={deleteCategory}>Delete</button>
              <button onClick={updateCategory}>Edit</button>
            </Col>
          </Row>
        </Container>
        {renderAddCategoryModal()}
        {renderUpdateCategoriesModal()}
        {renderDeleteCategoryModal()}
      </Layout>
    </div>
  )
}

export default Category
