import {
  LOGIN_REQUEST,
  LOGIN_REQUEST_FAIL,
  LOGIN_REQUEST_SUCCESS,
} from '../actions/auth/auth.types'

const initialState = {
  token: null,
  user: {
    name: '',
    email: '',
    picture: '',
  },
  authenticate: false,
  authenticating: false,
}

export const authReducer = (state = initialState, action) => {
  console.log(action)
  switch (action.type) {
    case LOGIN_REQUEST:
      return { ...state, authenticate: true }

    case LOGIN_REQUEST_SUCCESS:
      return { authenticate: true, authenticating: false }

    case LOGIN_REQUEST_FAIL:
      return { error: 'Invalid email or password' }

    default:
      return state
  }
}
