import { LOGIN_REQUEST } from './auth.types'
import axios from '../../helpers/axios'

export const login = (user) => async (dispatch) => {
  // const res = await axios.post("/admin/signin", {
  console.log(user)

  // })
  dispatch({
    type: LOGIN_REQUEST,
    payload: {
      ...user,
    },
  })
}
