import {
  ADD_NEW_CATEGORY_FAIL,
  ADD_NEW_CATEGORY_REQUEST,
  ADD_NEW_CATEGORY_SUCCESS,
  GET_ALL_CATEGORIES_SUCCESS,
} from '../actions/category/catergory.types'

const initState = {
  categories: [],
  loading: false,
  error: null,
}

const buildNewCategories = (categories, category) => {
  return categories.map((cate) => {
    if (cate._id === category.parentID) {
      category.children = []
      cate.children.push(category)
    }
    if (cate.children && cate.children.length > 0)
      buildNewCategories(cate.children, category)
    return cate
  })
}

export const categoryReducer = (state = initState, action) => {
  switch (action.type) {
    case GET_ALL_CATEGORIES_SUCCESS:
      return {
        ...state,
        categories: action.payload.categories,
        loading: false,
      }
    case ADD_NEW_CATEGORY_REQUEST:
      return {
        ...state,
        loading: true,
      }

    case ADD_NEW_CATEGORY_SUCCESS:
      const category = action.payload.category
      const updatedCategories = buildNewCategories(
        category.parentId,
        state.categories,
        category
      )
      console.log('updated categories', updatedCategories)
      return {
        ...state,
        categories: updatedCategories,
        loading: false,
      }

    case ADD_NEW_CATEGORY_FAIL:
      return {
        ...initState,
        error: action.payload.error,
      }

    default:
      return state
  }
}
