import {
  LOGIN_REQUEST,
  LOGIN_REQUEST_FAIL,
  LOGIN_REQUEST_SUCCESS,
} from '../actions/auth/auth.types'

const initialState = {
  token: null,
  user: {
    name: '',
    email: '',
    picture: '',
  },
  authenticate: false,
  authenticating: false,
}

export const authReducer = (state = initialState, action) => {
  console.log(action)
  switch (action.type) {
    case LOGIN_REQUEST:
      state = { ...state, authenticating: true }

    case LOGIN_REQUEST_SUCCESS:
      state = { loading: false, userInfo: action.payload, authenticate: true }

    case LOGIN_REQUEST_FAIL:
      state = { loading: false, error: 'Invalid email or password' }

    default:
      return state
  }
}
