import { LOGIN_REQUEST } from './auth.types'
import axios from 'axios'

export const login = (user) => async (dispatch) => {
  console.log(user)
  try {
    dispatch({
      type: LOGIN_REQUEST,
    })
    const config = {
      Headers: {
        'content-type': 'application/json',
      },
    }
    const { data } = await axios.post('/api/admin/signin', { user }, config)
  } catch (err) {
    console.log(err)
  }
}
