import axios from '../../helpers/axios'
import { LOGIN_REQUEST, LOGIN_REQUEST_SUCCESS } from './auth.types'

export const login = ({ user: email, password }) => async (dispatch) => {
  console.log(user)
  try {
    dispatch({
      type: LOGIN_REQUEST,
      payload: {
        email,
        password,
      },
    })
    // const config = {
    //   Headers: {
    //     'content-type': 'application/json',
    //   },
    // }
    const { data } = await axios.post(
      '/admin/signin',
      { email, password }
      // config
    )
    dispatch({
      type: LOGIN_REQUEST_SUCCESS,
      payload: data,
    })
    localStorage.setItem('userInfo', JSON.stringify(data))
  } catch (err) {
    console.error(err.response.data.errors)
  }
}
