import React, { useEffect } from 'react'
import { Link } from 'react-router-dom'
import { useDispatch, useSelector } from 'react-redux'
import { Row, Col, ListGroup, Image, Form, Button, Card } from 'react-bootstrap'
import Message from '../components/Message'
import { addToCart } from '../actions/cart/cart.action'

//we use history to redirect
export const Cartscreen = ({ match, location, history }) => {
  const productId = match.params.id

  //We use location to get a query string to get location?qty=1
  const qty = location.search ? Number(location.search.split('=')[1]) : 1

  const dispatch = useDispatch()

  const cart = useSelector((state) => state.cart)
  const { cartItems } = cart

  console.log(cartItems)

  useEffect(() => {
    if (productId) dispatch(addToCart(productId, qty))
  }, [dispatch, productId, qty])

  return (
    <Row>
      <Col md={8}>
        <h1>Shopping Cart</h1>
      </Col>
      {cartItems.length === 0 ? (
        <Message>
          Your cart is empty<Link to="/"> Go back</Link>
        </Message>
      ) : (
        <ListGroup variant="flish"></ListGroup>
      )}
      <Col md={2}></Col>
      <Col md={2}></Col>
    </Row>
  )
}

export default Cartscreen
